mapper.select:
select RESOURCE_ID resourceId, RESOURCE_TYPE resourceType, RESOURCE_NAME resourceName, RESOURCE_DESC resourceDesc, RESOURCE_PATH resourcePath, PRIORITY priority, enable enabled, (select m.MODULE_NAME from sys_modules_t m where m.MODULE_ID = t.MODULE_ID) moduleId, LAST_UPDATE_DATE lastUpdateDate from sys_resources_t t
ORDER BY ${sortFieldName} ${sortType}
ORDER BY T.LAST_UPDATE_DATE DESC

SELECT RESOURCE_ID resourceId, RESOURCE_TYPE resourceType, RESOURCE_NAME resourceName, RESOURCE_DESC resourceDesc, RESOURCE_PATH resourcePath, PRIORITY priority, MODULE_ID moduleId, ENABLE enabled FROM sys_resources_t t
1 = 1 and is_del = 0
and t.RESOURCE_NAME = #{resourceName}
and t.RESOURCE_PATH = #{resourcePath}
AND t.RESOURCE_ID = #{resourceId}

mapper.delete:
UPDATE sys_resources_t
SET IS_DEL = 1
WHERE RESOURCE_ID = #{resourceId}
