mapper.select:
SELECT MODULE_ID moduleId, MODULE_NAME moduleName, MODULE_DESC moduleDesc, MODULE_TYPE moduleType, PARENT parent, MODULE_URL moduleUrl, I_LEVEL level, ENABLE enable, PRIORITY priority, CREATE_DATE createDate, CREATOR creator, LAST_UPDATE_DATE updateTime, LAST_UPDATER updater FROM SYS_MODULES_T T
ORDER BY ${sortFieldName} ${sortType}
ORDER BY T.LAST_UPDATE_DATE DESC

sql.where:
  	1 = 1 and t.is_del = 0

  		and t.PARENT = #{parent}


  		and t.MODULE_TYPE = #{moduleType}


		and (t.MODULE_NAME like CONCAT('%',#{searchPhrase},'%') or t.MODULE_DESC like CONCAT('%',#{searchPhrase},'%'))

mapper.delete:
UPDATE sys_modules_t
SET IS_DEL = 1,
	LAST_UPDATE_DATE = NOW(),
	LAST_UPDATER = #{updater}
WHERE MODULE_ID = #{moduleId}

mapper.select:
SELECT MODULE_ID moduleId, MODULE_NAME moduleName, MODULE_DESC moduleDesc, MODULE_TYPE moduleType, PARENT parent, MODULE_URL moduleUrl, I_LEVEL level, ENABLE enable, PRIORITY priority, CREATE_DATE createDate, CREATOR creator, LAST_UPDATE_DATE updateTime, LAST_UPDATER updater FROM SYS_MODULES_T T
1 = 1 and t.is_del = 0
and t.MODULE_ID = #{moduleId}
